        foreach ($data as $value) {
            DeckLayout::create([
                'card_id' => $value['card_id'],
                'title' => $value['title'],
                'description' => $value['description'],
                'daily' => $value['daily'],
                'content_id' => $value['content_id'],
                'deck_id' => $value['deck_id'],
